import Vue from 'vue'
import chinaMap from './geoJson/china.json'
let vm = new Vue();
/**
 * 环形的饼图
 * @param {*} dom
 * @param {*} seriesName 图表名称
 * @param {*} formatterTitle 中间名称
 * @param {*} data 图表值
 * @param {*} total 图表中间值
 */
let pieChart = function (dom,seriesName,formatterTitle,data,total) {
    let myChart = vm.$echarts.init(dom);
    let option = {
        color: [
            "#5470c6",
            "#91cc75",
            "#fac858",
            "#ee6666",
            "#73c0de",
            "#3ba272",
            "#fc8452",
            "#9a60b4",
            "#ea7ccc",
        ],
        tooltip: {
            trigger: "item",
        },
        // legend: {
        //   top: '30%',
        //   left: '5%',
        //   orient:'vertial'
        // },
        series: [
            {
                name: seriesName||'seriesName',
                type: "pie",
                center: ["50%", "50%"],
                radius: ["60%", "70%"],
                itemStyle: {
                    borderRadius: 10,
                    borderColor: "#fff",
                    borderWidth: 2,
                },
                avoidLabelOverlap: false,
                label: {
                    show: true,
                    position: "center",
                    formatter: [`{a|${total || 0}}`, `{b|${formatterTitle|| 'formatterTitle'}}`].join("\n"),
                    rich: {
                        a: {
                            fontSize: "30px",
                            fontWeight: "bold",
                        },
                        b: {
                            color: "#666",
                            lineHeight: 40,
                        },
                    },
                },
                data:data||
                 [
                    { value: 1048, name: "18-30岁" },
                    { value: 735, name: "31-40岁" },
                    { value: 580, name: "41-50岁" },
                    { value: 300, name: "50-60岁" },
                    { value: 300, name: "60岁以上" },
                ],
            },
        ],
    };
    option && myChart.setOption(option);
}
/**
 *
 * @param {*} dom dom元素
 * @param {*} title 标题配置
 {
     text:'',
     fontSize:20,
     fontWright:'bold',
     color:''

 }
 * @param {*} seriesName 系列名称（展示在悬浮上的标题）
 * @param {*} legendData ['苹果', '香蕉', '橙子', '梨子', '西瓜']
 * @param {*} seriesData [
                            { value: 335, name: '苹果' },
                            { value: 310, name: '香蕉' },
                            { value: 234, name: '橙子' },
                            { value: 135, name: '梨子' },
                            { value: 1548, name: '西瓜' }
                        ]
 */
let normalPieChart = function(dom,title,seriesName,legendData,seriesData){
    let myChart = vm.$echarts.init(dom);

    // 饼图的数据
    var data = [
        { value: 335, name: '苹果' },
        { value: 310, name: '香蕉' },
        { value: 234, name: '橙子' },
        { value: 135, name: '梨子' },
        { value: 1548, name: '西瓜' }
    ];

    // 饼图的配置项
    var option = {
        title: {
            text: title.text || '标题',
            left: 'center',
            textStyle:{
                fontWeight:title.fontWeight||'bold',
                fontSize:title.fontSize || 18,
                color: '#2b7'
            }
        },
        tooltip: {
            trigger: 'item',
            formatter: '{a} <br/>{b} : {c} ({d}%)'
        },
        legend: {
            orient: 'vertical',
            left: 'left',
            data: legendData|| ['苹果', '香蕉', '橙子', '梨子', '西瓜']
        },
        series: [
            {
                name: seriesName||'销售量',
                type: 'pie',
                radius: '55%',
                center: ['50%', '60%'],
                data: seriesData || data,
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
            }
        ]
    };

    // 使用配置项设置图表
    option && myChart.setOption(option);
}
/**
 * 折线图
 * @param {*} dom dom元素
 * @param {*} title 图表标题配置
 title: {
    text: '标题'
    fontSize:13
    fontWeight:'bold'
    color:'#2b7'
  }
 * @param {*} seriesName 系列名称
 * @param {*} yAxis 纵坐标配置{}
 * @param {*} xAxisData 横坐标值
 * @param {*} seriesData 图表值
 * @param {*} type line OR bar
 */
let lineChart = function (dom,title,seriesName,yAxis,xAxisData,seriesData,type) {
    let myChart = vm.$echarts.init(dom);
    let option = {
        title:{
            text:title.text||'标题',
            left:'center',
            textStyle:{
                fontSize: title.fontSize || 13,
                fontWeight: title.fontWeight || 'bold',
                color:title.color|| '#2b7'
            }
        },
        // legend: {
        //     data: [seriesName||'seriesName']
        // },
        tooltip: {
            trigger: 'axis'
          },
        grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true
        },
        xAxis: [
            {
                type: 'category',
                data: xAxisData||['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
                axisPointer: {
                    type: 'shadow'
                }
            }
        ],
        yAxis: [
            {
                type: 'value',
                name: yAxis.name||'yAxisName',
                min: yAxis.min||0,
                max:yAxis.max|| 250,
                interval: yAxis.interval || 50,
                axisLabel: {
                    formatter: '{value}'
                }
            }
        ],
        series: [
            {
                name: seriesName||'seriesName',
                type: type||'bar',
                data:seriesData|| [
                    2.0, 4.9, 7.0, 23.2, 25.6, 76.7, 135.6, 162.2, 32.6, 20.0, 6.4, 3.3
                ]
            }
        ]
    };
    option && myChart.setOption(option);
}



