/**
 * 地图
 * @param {*} dom
 * @param {*} data
 * @param {*} i
 * @param {*} bgColor
 * @param {*} lineColor
 * @param {*} title配置型
 {
     text :这边暂时没用到，写死的，因为地图主要用于溯源统计
     fontSize:20
     fontSize:13
    fontWeight:'bold'
    color:'#2b7'
 }
 */
let mapChart = function(dom,data,i,bgColor,lineColor,title){
    let myChart = vm.$echarts.init(dom);
    vm.$echarts.registerMap("china", { geoJSON: chinaMap });
    // let data =[{name:'yc',value:[117.37,32.94,3000]}]

       var option = {
           title:{
               text:'地图统计图表',
               subtext:`我们的产品走过了${data.length}个城市，被客户查询了${i}次`,
               x:'center',
               y:'top',
               textStyle:{
                   color:title.color||'#2b7',
                   fontSize:title.fontSize||20,
                   fontWeight:title.fontWeight||'bold'
               },
               subtextStyle:{
                color:'#C0C4CC',
                fontSize:'15',
                height:'40',
                lineHeight:'40'
                }
           },
           toolbox:{},
        //    legend:{
        //        x:'left',
        //        y:'top'
        //    },
           visualMap:{
               min:0,
               max:1000,
               show:true,
               type:'piecewise',
               pieces:[
              {gt: 1500},            // (1500, Infinity]
               {gt: 900, lte: 1500},  // (900, 1500]
               {gt: 310, lte: 1000},  // (310, 1000]
               {gt: 200, lte: 300},   // (200, 300]
               {gt: 0, lte: 200, color:'#2b7'}, ],
               textStyle: {
                color: lineColor?'#ffffff':'#666'
              },
               },
           geo:{
               map:'china',
               roam:true,
               show:true,
               zoom:1.3,
               top:250,
               select:{
                disabled:false
               },
               itemStyle:{
                   borderColor:'#fff',
                   borderWidth:2,
                   areaColor:bgColor || '#e6e8eb'
               }
           },
           tooltip:{
               show:true,
               formatter:function(param,ticket,callback){
                   return `在${param.data.name}查询了${param.data.value[2]}次`
               }
           },
           series:[
            //    {
            //        type:'heatmap',
            //        coordinateSystem:'geo',
            //        data:data,
            //        name:'热力图'
            //    },
               {
                   type:'scatter',
                   symbol:'image://data:image/png;base64,',
                   symbolSize:'28',
                   coordinateSystem:'geo',
                   data:data,
                   label:{
                       show:false
                   }
               }
           ],
       };
    option && myChart.setOption(option);
}

export { pieChart,normalPieChart,lineChart,mapChart }
