mapper.select:
SELECT t.* ,b.batch_name FROM agriculture_batch_task t LEFT JOIN agriculture_crop_batch b on t.batch_id = b.batch_id

     and b.batch_head = ${params.batchHead} 
     and t.batch_id = #{batchId}
     and t.task_name like concat('%', #{taskName}, '%')
     and DATE_FORMAT(plan_start,'%Y-%m-%d') Between '${params.planStartStart}' AND '${params.planStartEnd}' 
     and t.plan_finish = #{planFinish}
     and t.actual_start = #{actualStart}
     and t.actual_finish = #{actualFinish}
     and t.task_detail = #{taskDetail}
     and t.task_images = #{taskImages}
     and t.task_videos = #{taskVideos}
     and t.status = #{status}
     and t.order_num = #{orderNum}
    and t.del_flag = 0

mapper.update:
update agriculture_batch_task
task_head = #{taskHead}, task_head_name = #{taskHeadName}, task_name = #{taskName}, plan_start = #{planStart}, plan_finish = #{planFinish}, actual_start = #{actualStart}, actual_finish = #{actualFinish}, task_detail = #{taskDetail}, task_images = #{taskImages}, task_videos = #{taskVideos}, remark = #{remark}, status = #{status}, order_num = #{orderNum}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where task_id = #{taskId}
